import * as actionTypes from "../constants/cart.constant";

const initialState = {
    products: [
    ],
    totalPrice: 0,
};

export const cartReducer = (state = initialState, action) => {
    console.log(action.type)

    switch (action.type) {
        case actionTypes.ADD_TO_CART:
            
            return{
                ...state,
                products: [
                    ...state.products,
                    {
                        product: {...action.payload.product},
                        count: 1
                    }
                ],
                totalPrice: state.totalPrice + action.payload.totalPrice
            }
        case actionTypes.UPDATE_CART:
            const products = state.products
            const index = products.findIndex(item => item._id === action.payload.product._id);

            products[index].count = products[index].count + 1;

            return{
                ...state,
                products: [
                    ...state.products
                ]
            }

        default:
            return state;
    }
};
