import React, { useContext } from "react";
import "../assets/css/SideBar.css";
import classNames from "classnames";
import { AppContext } from "../contexts/AppProvider";

export default function SideBar(props) {
    const { isVisibleSidebar, setIsVisibleSidebar } = useContext(AppContext);

    const SideBarClassName = classNames("sidebar-wrapper", {
        active: isVisibleSidebar,
    });
    return (
        <div className={SideBarClassName}>
            <div className='sidebar-header'>
                <span>{props.title}</span>
                <i class='fal fa-times' onClick={() => setIsVisibleSidebar(false)}></i>
            </div>
            <div className='sidebar-body'>{props.content}</div>
        </div>
    );
}
