import * as actionTypes from '../constants/cart.constant';
import { toast } from "react-toastify";

export const addToCart = (item) => async (dispatch, getState) => {
    const cart = getState().cart.products;

    if (cart.length !== 0){ //cart is not null
        const index = cart.findIndex(product => product.product._id === item._id);
        if (index >= 0){ //item exist in cart
            dispatch({
                type: actionTypes.UPDATE_CART,
                payload: {
                    product: item
                }
            })
        }else{ //item is not exist in cart
            dispatch({
                type: actionTypes.ADD_TO_CART,
                payload: {
                    product: item,
                    totalPrice: item.price,
                },
            });
        }
    }else{
        dispatch({
            type: actionTypes.ADD_TO_CART,
            payload: {
                product: item,
                totalPrice: item.price
            }
        })
    }

    toast.success('Đã thêm vào giỏ hàng')
}

export const updateCart = (count, item) => async (dispatch, getState) => {
    dispatch({
        type: actionTypes.UPDATE_CART,
        payload: {
            count: count,
            product: item
        }
    })
}
