import * as actionTypes from "../constants/cart.constant";

const initialState = {
    products: [],
    totalPrice: 0,
};

export const cartReducer = (state = initialState, action) => {
    switch (action.type) {
        case actionTypes.ADD_TO_CART:
            return {
                ...state,
                products: [
                    ...state.products,
                    {
                        product: { ...action.payload.product },
                        count: 1,
                    },
                ],
                totalPrice: state.totalPrice + action.payload.totalPrice,
            };

        case actionTypes.UPDATE_CART:
            const products = [...state.products];
            const index = products.findIndex(
                (item) => item.product._id === action.payload.product._id
            );
            products[index].count = products[index].count + action.payload.count;

            return {
                ...state,
                products: [...products],
                totalPrice: state.totalPrice + action.payload.product.price,
            };
        case actionTypes.REMOVE_FROM_CART:
            
        default:
            return state;
    }
};
