import * as actionTypes from "../constants/auth.constant";
import axios from "axios";
import jwt_decode from "jwt-decode";

export const login = (username, password) => async (dispatch, getState) => {
    axios
        .post("/auth/login", {
            username,
            password,
        })
        .then((res) => {
            const { data } = res;

            if (!data.message) {
                dispatch({
                    type: actionTypes.LOGIN_REQUIRED,
                });

                dispatch({
                    type: actionTypes.LOGIN_SUCCESS,
                    payload: {
                        accessToken: data.accessToken,
                    },
                });

                localStorage.setItem(
                    "accessToken",
                    JSON.stringify(getState().auth.accessToken)
                );

                // localStorage.setItem("user", JSON.stringify(getState().auth.user));
            } else {
                dispatch({
                    type: actionTypes.LOGIN_FAIL,
                    payload: {
                        message: data.message,
                    },
                });
            }
        }).catch(err => {
            
        });

    return JSON.stringify(getState().auth.accessToken);
};
