import * as actionTypes from '../constants/product.constant'

const initialState = {
    products: [],
    isLoading: false
};

export const productReducer = (state = initialState, action) => {
    switch(action.type){
        case actionTypes.GET_PRODUCT_REQUIRED:
            return {
                ...state,
                isLoading: true
            }
        case actionTypes.GET_PRODUCT_BY_CATEGORY_NAME:
            return{
                ...state,
                isLoading: false,
                products: [
                    ...action.payload
                ]
            }
        default:
            return state
    }
}
