import React, {useState} from 'react'

const AppContext = React.createContext();

export default function AppProvider({child}) {
    const [isAppHover, setIsAppHover] = useState(false);

    return (
        <AppContext.Provider value={isAppHover, setIsAppHover}>
            {child}
        </AppContext.Provider>
    )
}
